mapper.select:
SELECT NNO, WRITER, TITLE, CONTENT, HIT, IP, NDATE, NLASTMODIFIED
  FROM NOTICE
 WHERE NNO = #{nNo}

mapper.update:
UPDATE NOTICE
   SET HIT = HIT + 1
 WHERE NNO = #{nNo}

UPDATE NOTICE
   SET TITLE = #{TITLE}, CONTENT = #{CONTENT}, NLASTMODIFIED = SYSDATE
 WHERE NNO = #{nNo}

mapper.select:
SELECT NNO, WRITER, TITLE, CONTENT, HIT, IP, NDATE, NLASTMODIFIED
  FROM NOTICE
 WHERE ${column} LIKE #{query}
 		WRITER LIKE #{query} OR TITLE LIKE #{query} OR CONTENT LIKE #{query}
